import React, { useEffect, useState } from "react";

const ReviewList = ({ movieId }) => {
  const [reviews, setReviews] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchReviews = async () => {
      const token = localStorage.getItem("token"); // Retrieve token from localStorage
      try {
        const response = await fetch(
          `http://localhost:8080/movies/${movieId}/reviews`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${token}`, // Include token in request headers
            },
          }
        );

        if (!response.ok) {
          if (response.status === 401) {
            throw new Error("Unauthorized");
          } else {
            throw new Error("Network response was not ok");
          }
        }

        const data = await response.json();
        setReviews(data.data || []); // Ensure data is set to an array
      } catch (error) {
        console.error("Error fetching reviews:", error);
        setError("Error fetching reviews: " + error.message);
      }
    };

    fetchReviews();
  }, [movieId]);

  if (error) {
    return <p className="error">{error}</p>;
  }

  if (reviews.length === 0) {
    return <p>No reviews available for this movie.</p>;
  }

  return (
    <div className="review-list">
      {reviews.map((review) => (
        <div key={review.id} className="review">
          <p>
            <strong>User:</strong> {review.user}
          </p>
          <p>
            <strong>Message:</strong> {review.message}
          </p>
          <p>
            <strong>Post Date:</strong> {review.rating}
          </p>
          <p>
            <strong>Likes:</strong> {review.likes}
          </p>
        </div>
      ))}
    </div>
  );
};

export default ReviewList;
