import React, { useState, useEffect } from "react";
import AddReview from "./AddReview";

// Dummy data for users
const users = [
  { id: 1, name: "John Doe" },
  { id: 2, name: "Jane Smith" },
  { id: 3, name: "Alice Johnson" },
];

// Function to get user by user ID
const getUserById = (userId) => users.find((user) => user.id === userId);

const ReviewList = ({ movieId }) => {
  const [reviews, setReviews] = useState([]);
  const [likeCounts, setLikeCounts] = useState({});

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch(
          `http://localhost:8080/movies/${movieId}/reviews`
        );
        const result = await response.json();
        const data = result.data; // Accesarea array-ului de review-uri
        console.log(data);

        setReviews(data);
        const initialLikeCounts = data.reduce((acc, review) => {
          acc[review.id] = review.likes;
          return acc;
        }, {});
        setLikeCounts(initialLikeCounts);
      } catch (error) {
        console.error("Error fetching reviews:", error);
      }
    };

    fetchReviews();
  }, [movieId]);

  const handleLikeClick = async (reviewId) => {
    try {
      const response = await fetch(
        `http://localhost:8080/movies/${movieId}/reviews/${reviewId}/like`,
        {
          method: "POST",
        }
      );

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      // Assuming the server returns the updated like count
      const data = await response.json();

      setLikeCounts((prevState) => ({
        ...prevState,
        [reviewId]: data.likes,
      }));
    } catch (error) {
      console.error("Error updating like count:", error);
    }
  };

  const handleAddReview = (newReview) => {
    const newReviewId = reviews.length ? reviews[reviews.length - 1].id + 1 : 1;
    const reviewToAdd = {
      ...newReview,
      id: newReviewId,
      userId: 1,
      likes: 0,
    };
    setReviews((prevState) => [...prevState, reviewToAdd]);
    setLikeCounts((prevState) => ({
      ...prevState,
      [newReviewId]: 0,
    }));
  };

  return (
    <div>
      <h2>Reviews</h2>
      <AddReview onAddReview={handleAddReview} />
      <div style={{ maxHeight: "300px", overflowY: "scroll" }}>
        {reviews.map((review) => (
          <div
            key={review.id}
            style={{
              border: "1px solid black",
              padding: "10px",
              marginBottom: "20px",
            }}
          >
            <p>Message: {review.message}</p>
            <p>User: {getUserById(review.userId)?.name || "Unknown User"}</p>
            <p>Likes: {likeCounts[review.id]}</p>
            <button onClick={() => handleLikeClick(review.id)}>Like</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ReviewList;
