import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar } from "@fortawesome/free-solid-svg-icons";
import "./index.css"; // Importul fișierului CSS pentru stilizare

const MoviePage = () => {
  const movie = {
    title: "Numele Filmului",
    releaseDate: "01 ianuarie 2024",
    description: "Descrierea filmului va fi disponibilă în curând...",
    director: "Regizorul va fi anunțat în curând",
    rating: 0,
    reviews: [],
    imageUrl: "https://via.placeholder.com/400x600",
  };
  return (
    <>
      <NavBar></NavBar>
      <div className="movie-page">
        <div className="movie-details">
          <div className="movie-poster">
            <img src={movie.imageUrl} alt="Poster film" />
          </div>
          <div className="movie-info">
            <h1 className="movie-title">{movie.title}</h1>
            <p className="movie-release-date">
              Data lansării: {movie.releaseDate}
            </p>
            <p>{movie.description}</p>
            <p>Regia: {movie.director}</p>
            <p>
              Rating:{" "}
              {[...Array(Math.floor(movie.rating))].map((_, index) => (
                <FontAwesomeIcon icon={faStar} key={index} />
              ))}
              {movie.rating}
            </p>
            <h2>Recenzii</h2>
            <ul className="reviews">
              {movie.reviews.map((review) => (
                <li key={review.id}>
                  <p>{review.user}</p>
                  <p>{review.comment}</p>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </>
  );
};

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">🍿</span>
      <h1>Movre</h1>
    </div>
  );
}

export default MoviePage;
