import { useEffect, useRef, useState } from "react";
import StarRating from "./StarRating";
import ReviewList from "./ReviewList";

export default function MovieDetails({
  selectedId,
  onCloseMovie,
  onAddWatched,
  watched,
}) {
  const [movie, setMovie] = useState({});
  const [userRating, setUserRating] = useState("");

  const countRef = useRef(0);

  useEffect(
    function () {
      if (userRating) countRef.current++;
    },
    [userRating]
  );

  const isWatched = watched.map((movie) => movie.id).includes(selectedId);
  const watchedUserRating = watched.find(
    (movie) => movie.id === selectedId
  )?.userRating;

  function handleAdd() {
    const newWatchedMovie = movie;
    onAddWatched(newWatchedMovie);
    onCloseMovie();
  }

  useEffect(
    function () {
      function callback(e) {
        if (e.code === "Escape") {
          onCloseMovie();
        }
      }

      document.addEventListener("keydown", callback);

      return function () {
        document.removeEventListener("keydown", callback);
      };
    },
    [onCloseMovie]
  );

  useEffect(
    function () {
      async function getMovieDetails() {
        const res = await fetch(
          `http://localhost:8080/movies/?id=${selectedId}`
        );
        const data = await res.json();
        setMovie(data.data[0]);
        console.log(movie);
      }
      getMovieDetails();
    },
    [selectedId]
  );

  useEffect(
    function () {
      if (!movie.name) return;
      document.name = `Movie | ${movie.name}`;

      return function () {
        document.name = "usePopcorn";
      };
    },
    [movie.name]
  );

  return (
    <div
      style={{
        overflow: "auto",
        height: "200px",
        border: "1px solid #ccc",
        padding: "10px",
      }}
    >
      >
      <div className="details">
        <>
          <header>
            <button className="btn-back" onClick={onCloseMovie}>
              &larr;
            </button>
            <img src={movie.picture} alt={`Poster of ${movie.name} movie`} />
            <div className="details-overview">
              <h2>{movie.name}</h2>
              <p>
                {movie.release_year} &bull; {movie.runtime}
              </p>
              <p>
                <span>⭐️</span>
                {movie.rating} Rating
              </p>
              <p>{movie.type}</p>
              <p>
                <em>{movie.description}</em>
              </p>
            </div>
          </header>

          {/* <p>{avgRating}</p> */}

          <section>
            <div className="rating">
              {!isWatched ? (
                <>
                  <StarRating
                    maxRating={10}
                    size={24}
                    onSetRating={setUserRating}
                  />
                  {userRating > 0 && (
                    <button className="btn-add" onClick={handleAdd}>
                      + Add to list
                    </button>
                  )}
                </>
              ) : (
                <p>
                  You rated with movie {watchedUserRating} <span>⭐️</span>
                </p>
              )}
            </div>
          </section>
        </>
      </div>
      <ReviewList />
    </div>
  );
}

function Loader() {
  return <p className="loader">Loading...</p>;
}
