import React, { useState } from "react";

export default function AdminLogin({ updateLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleAdminLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8080/auth/admin-login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        const token = data.token; // Extract token from response

        // Save token in localStorage
        localStorage.setItem("adminToken", token);
        console.log(token);

        // Update login state
        updateLogin(true);
      } else {
        console.error("Autentificarea admin a eșuat!"); // Romanian message for failed admin login
      }
    } catch (error) {
      console.error("Eroare de rețea:", error); // Romanian message for network error
    }
  };

  return (
    <div className="admin-login-form">
      <form onSubmit={handleAdminLogin}>
        <div>
          <label>
            Email:
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Password:
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </label>
        </div>
        <button type="submit">Admin Login</button>
      </form>
    </div>
  );
}
