import { useEffect, useRef, useState } from "react";
import StarRating from "./StarRating";

export default function MovieDetails({
  selectedId,
  onCloseMovie,
  onAddWatched,
  watched,
}) {
  const [movie, setMovie] = useState({});
  const [userRating, setUserRating] = useState("");

  const countRef = useRef(0);

  useEffect(
    function () {
      if (userRating) countRef.current++;
    },
    [userRating]
  );

  const isWatched = watched.map((movie) => movie.id).includes(selectedId);
  const watchedUserRating = watched.find(
    (movie) => movie.id === selectedId
  )?.userRating;

  function handleAdd() {
    const newWatchedMovie = {
      Id: id,
      name,
      release_year,
      picture,
      runtime: Number(runtime.split(" ").at(0)),
      userRating,
      countRatingDecisions: countRef.current,
    };

    onAddWatched(newWatchedMovie);
    onCloseMovie();
  }

  useEffect(
    function () {
      function callback(e) {
        if (e.code === "Escape") {
          onCloseMovie();
        }
      }

      document.addEventListener("keydown", callback);

      return function () {
        document.removeEventListener("keydown", callback);
      };
    },
    [onCloseMovie]
  );

  useEffect(
    function () {
      async function getMovieDetails() {
        const res = await fetch(
          `http://localhost:8080/movies/?id=${selectedId}`
        );
        const data = await res.json();
        setMovie(data.data[0]);
      }
      getMovieDetails();
    },
    [selectedId]
  );

  useEffect(
    function () {
      if (!name) return;
      document.name = `Movie | ${name}`;

      return function () {
        document.name = "usePopcorn";
      };
    },
    [name]
  );

  return (
    <div className="details">
      <>
        <header>
          <button className="btn-back" onClick={onCloseMovie}>
            &larr;
          </button>
          <img src={picture} alt={`Poster of ${movie} movie`} />
          <div className="details-overview">
            <h2>{"Interstelar"}</h2>
            <p>
              {"2024"} &bull; {"190"}
            </p>
            <p>
              <span>⭐️</span>
              {"7"} Rating
            </p>
            <p>{"Action"}</p>
            <p>
              <em>{type}</em>
            </p>
          </div>
        </header>

        {/* <p>{avgRating}</p> */}

        <section>
          <div className="rating">
            {!isWatched ? (
              <>
                <StarRating
                  maxRating={10}
                  size={24}
                  onSetRating={setUserRating}
                />
                {userRating > 0 && (
                  <button className="btn-add" onClick={handleAdd}>
                    + Add to list
                  </button>
                )}
              </>
            ) : (
              <p>
                You rated with movie {watchedUserRating} <span>⭐️</span>
              </p>
            )}
          </div>
        </section>
      </>
    </div>
  );
}

function Loader() {
  return <p className="loader">Loading...</p>;
}
