import React, { useState } from "react";
import AddReview from "./AddReview";

// Dummy data for reviews
const initialReviews = [
  { id: 1, userId: 1, message: "Great movie!", likes: 0 },
  { id: 2, userId: 2, message: "Could have been better", likes: 0 },
  { id: 3, userId: 3, message: "Disappointing...", likes: 0 },
  { id: 4, userId: 1, message: "Another great movie!", likes: 0 },
];

// Function to get user by user ID
const getUserById = (userId) => users.find((user) => user.id === userId);

const ReviewList = () => {
  const [reviews, setReviews] = useState(initialReviews);
  const [likeCounts, setLikeCounts] = useState(
    initialReviews.reduce((acc, review) => {
      acc[review.id] = review.likes;
      return acc;
    }, {})
  );

  const handleLikeClick = (reviewId) => {
    setLikeCounts((prevState) => ({
      ...prevState,
      [reviewId]: prevState[reviewId] + 1,
    }));
  };

  const handleAddReview = (newReview) => {
    const newReviewId = reviews.length ? reviews[reviews.length - 1].id + 1 : 1;
    const reviewToAdd = {
      ...newReview,
      id: newReviewId,
      userId: 1, // Assuming the userId is 1 for now. You can change it as needed.
      likes: 0,
    };
    setReviews((prevState) => [...prevState, reviewToAdd]);
    setLikeCounts((prevState) => ({
      ...prevState,
      [newReviewId]: 0,
    }));
  };

  return (
    <div>
      <h2>Reviews</h2>
      <AddReview onAddReview={handleAddReview} />
      <div style={{ maxHeight: "300px", overflowY: "scroll" }}>
        {reviews.map((review) => (
          <div
            key={review.id}
            style={{
              border: "1px solid black",
              padding: "10px",
              marginBottom: "20px",
            }}
          >
            <p>Message: {review.message}</p>
            <p>User: {getUserById(review.userId).name}</p>
            <p>Likes: {likeCounts[review.id]}</p>
            <button onClick={() => handleLikeClick(review.id)}>Like</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ReviewList;
