import React, { useState } from "react";

const Review = ({ review, movieId }) => {
  const [likes, setLikes] = useState(review.like);
  const [hasLiked, setHasLiked] = useState(false);

  const handleLike = async () => {
    if (hasLiked) return;

    const token = localStorage.getItem("token");

    try {
      const response = await fetch(
        `http://localhost:8080/movies/${movieId}/reviews/${review.id}/like`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }

      setLikes(likes + 1);
      setHasLiked(true);
    } catch (error) {
      console.error("Error liking review:", error);
    }
  };

  const reviewStyle = {
    border: "1px solid #ccc",
    padding: "15px",
    marginBottom: "15px",
    borderRadius: "5px",
    backgroundColor: "#f9f9f9",
  };

  return (
    <div style={reviewStyle}>
      <p>
        <strong>User:</strong> {review.user}
      </p>
      <p>
        <strong>Message:</strong> {review.message}
      </p>
      <p>
        <strong>Date:</strong> {new Date(review.createdAt).toLocaleDateString()}
      </p>
      <p>
        <strong>Likes:</strong> {likes}
      </p>
      <button onClick={handleLike} disabled={hasLiked}>
        {hasLiked ? "Liked" : "Like"}
      </button>
    </div>
  );
};

export default Review;
