import { useEffect, useRef, useState } from "react";
import StarRating from "./StarRating";
import ReviewList from "./ReviewList";
import AddReview from "./AddReview";

export default function MovieDetails({
  selectedId,
  onCloseMovie,
  onAddWatched,
  watched,
}) {
  const [movie, setMovie] = useState({});
  const [reviews, setReviews] = useState([]);
  const [userRating, setUserRating] = useState("");

  const countRef = useRef(0);

  useEffect(() => {
    if (userRating) countRef.current++;
  }, [userRating]);

  const isWatched = watched.map((movie) => movie.id).includes(selectedId);
  const watchedUserRating = watched.find(
    (movie) => movie.id === selectedId
  )?.userRating;

  async function handleAdd() {
    const token = localStorage.getItem("token"); // Retrieve token from localStorage

    try {
      // First request to save the rating
      const ratingResponse = await fetch(
        `http://localhost:8080/movies/${selectedId}/rating`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // Include token in request headers
          },
          body: JSON.stringify({
            star: userRating,
          }),
        }
      );

      if (!ratingResponse.ok) {
        throw new Error("Network response was not ok for rating");
      }

      // Second request to add to watch list
      const watchListResponse = await fetch(
        `http://localhost:8080/movies/${selectedId}/watchList`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // Include token in request headers
          },
        }
      );

      if (!watchListResponse.ok) {
        throw new Error("Network response was not ok for watch list");
      }

      onCloseMovie();
    } catch (error) {
      console.error("Error adding rating or watch list:", error);
    }
  }

  useEffect(() => {
    function callback(e) {
      if (e.code === "Escape") {
        onCloseMovie();
      }
    }

    document.addEventListener("keydown", callback);

    return function () {
      document.removeEventListener("keydown", callback);
    };
  }, [onCloseMovie]);

  useEffect(() => {
    async function getMovieDetails() {
      const token = localStorage.getItem("token"); // Retrieve token from localStorage
      try {
        const res = await fetch(
          `http://localhost:8080/movies/?id=${selectedId}`,
          {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${token}`, // Include token in request headers
            },
          }
        );
        const data = await res.json();
        setMovie(data.data[0] || {}); // Ensure data is set to an object
      } catch (error) {
        console.error("Error fetching movie details:", error);
      }
    }
    getMovieDetails();
  }, [selectedId]);

  useEffect(() => {
    if (!movie.name) return;
    document.title = `Movie | ${movie.name}`;

    return function () {
      document.title = "usePopcorn";
    };
  }, [movie.name]);

  const handleAddReview = (newReview) => {
    setReviews((prevReviews) => [...prevReviews, newReview]);
  };

  return (
    <div>
      <div className="details">
        <>
          <header>
            <button className="btn-back" onClick={onCloseMovie}>
              &larr;
            </button>
            <img src={movie.imageUrl} alt={`Poster of ${movie.name} movie`} />
            <div className="details-overview">
              <h2>{movie.name}</h2>
              <p>
                {movie.release_year} &bull; {movie.runtime}
              </p>
              <p>
                <span>⭐️</span>
                {movie.raiting} Rating
              </p>
              <p>{movie.type}</p>
              <p>
                <em>{movie.description}</em>
              </p>
            </div>
          </header>

          <section>
            <div className="rating">
              {!isWatched ? (
                <>
                  <StarRating
                    maxRating={10}
                    size={24}
                    onSetRating={setUserRating}
                  />
                  {userRating > 0 && (
                    <button className="btn-add" onClick={handleAdd}>
                      + Add to list
                    </button>
                  )}
                </>
              ) : (
                <p>
                  You rated this movie {watchedUserRating} <span>⭐️</span>
                </p>
              )}
            </div>
          </section>
        </>
      </div>
      <AddReview movieId={selectedId} onAddReview={handleAddReview} />
      <ReviewList movieId={selectedId} reviews={reviews} />
    </div>
  );
}

function Loader() {
  return <p className="loader">Loading...</p>;
}
